INSERT INTO `rex_module` VALUES 
  (1,'01 . YForm Formbuilder','<?php\r\n\r\n/**\r\n * yform\r\n * @author jan.kristinus[at]redaxo[dot]org Jan Kristinus\r\n * @author <a href=\"http://www.yakamara.de\">www.yakamara.de</a>\r\n */\r\n\r\n// module:yform_basic_output\r\n// v1.0.1\r\n//--------------------------------------------------------------------------------\r\n\r\n$yform = new rex_yform;\r\nif (\"REX_VALUE[7]\" == 1) { $yform->setDebug(TRUE); }\r\n$form_data = \"REX_VALUE[id=3 output=html]\";\r\n$form_data = trim(rex_yform::unhtmlentities($form_data));\r\n$yform->setObjectparams(\'form_action\', rex_getUrl(REX_ARTICLE_ID,REX_CLANG_ID));\r\n$yform->setFormData($form_data);\r\n\r\n// action - showtext\r\nif(\"REX_VALUE[id=11]\" != \"\") {\r\n    $html = \"0\"; // plaintext\r\n    if(\'REX_VALUE[11]\' == 1) $html = \"1\"; // html\r\n    if(\'REX_VALUE[11]\' == 2) $html = \"2\"; // textile\r\n\r\n    $e3 = \'\'; $e4 = \'\';\r\n    if ($html == \"0\") {\r\n        $e3 = \'<div class=\"alert alert-success\">\';\r\n        $e4 = \'</div>\';\r\n    }\r\n\r\n    $t = str_replace(\"<br />\",\"\",rex_yform::unhtmlentities(\'REX_VALUE[6]\'));\r\n    $yform->setActionField(\"showtext\",array(\r\n    $t,\r\n    $e3,\r\n    $e4,\r\n    $html // als HTML interpretieren\r\n    )\r\n    );\r\n}\r\n\r\n$form_type = \"REX_VALUE[1]\";\r\n\r\n// action - email\r\nif ($form_type == \"1\" || $form_type == \"2\") {\r\n    $mail_from = (\'REX_VALUE[2]\' != \'\') ? \'REX_VALUE[2]\' : rex::getErrorEmail();\r\n    $mail_to = (\'REX_VALUE[12]\' != \'\') ? \'REX_VALUE[12]\' : rex::getErrorEmail();\r\n    $mail_subject = \"REX_VALUE[4]\";\r\n    $mail_body = str_replace(\"<br />\",\"\",rex_yform::unhtmlentities(\'REX_VALUE[5]\'));\r\n    $yform->setActionField(\"email\", array(\r\n    $mail_from,\r\n    $mail_to,\r\n    $mail_subject,\r\n    $mail_body\r\n    )\r\n    );\r\n}\r\n\r\n// action - db\r\nif ($form_type == \"0\" || $form_type == \"2\") {\r\n    $yform->setObjectparams(\'main_table\', \'REX_VALUE[8]\');\r\n\r\n    //getdata\r\n    if (\"REX_VALUE[10]\" != \"\")\r\n        $yform->setObjectparams(\"getdata\",TRUE);\r\n\r\n    $yform->setActionField(\"db\", array(\r\n    \"REX_VALUE[8]\", // table\r\n    $yform->objparams[\"main_where\"], // where\r\n    )\r\n    );\r\n}\r\n\r\necho $yform->getForm();\r\n?>','<?php\r\n\r\n/**\r\n * yform\r\n * @author jan.kristinus[at]redaxo[dot]org Jan Kristinus\r\n * @author <a href=\"http://www.yakamara.de\">www.yakamara.de</a>\r\n */\r\n\r\n// module:yform_basic_input\r\n// v1.0\r\n// --------------------------------------------------------------------------------\r\n\r\n// DEBUG SELECT\r\n////////////////////////////////////////////////////////////////////////////////\r\n$dbg_sel = new rex_select();\r\n$dbg_sel->setName(\'REX_INPUT_VALUE[7]\');\r\n$dbg_sel->setAttribute(\'class\', \'form-control\');\r\n$dbg_sel->addOption(\'inaktiv\',\'0\');\r\n$dbg_sel->addOption(\'aktiv\',\'1\');\r\n$dbg_sel->setSelected(\'REX_VALUE[7]\');\r\n$dbg_sel = $dbg_sel->get();\r\n\r\n\r\n// TABLE SELECT\r\n////////////////////////////////////////////////////////////////////////////////\r\n$gc = rex_sql::factory();\r\n$gc->setQuery(\'SHOW TABLES\');\r\n$tables = $gc->getArray();\r\n$tbl_sel = new rex_select();\r\n$tbl_sel->setName(\'REX_INPUT_VALUE[8]\');\r\n$tbl_sel->setAttribute(\'class\', \'form-control\');\r\n$tbl_sel->addOption(\'Keine Tabelle ausgewählt\', \'\');\r\nforeach ($tables as $key => $value) {\r\n  $tbl_sel->addOption(current($value), current($value));\r\n}\r\n$tbl_sel->setSelected(\'REX_VALUE[8]\');\r\n$tbl_sel = $tbl_sel->get();\r\n\r\n\r\n// PLACEHOLDERS\r\n////////////////////////////////////////////////////////////////////////////////\r\n$yform = new rex_yform;\r\n$form_data = \'REX_VALUE[3]\';\r\n$form_data = trim(str_replace(\'<br />\',\'\',rex_yform::unhtmlentities($form_data)));\r\n$yform->setFormData($form_data);\r\n$yform->setRedaxoVars(REX_ARTICLE_ID,REX_CLANG_ID);\r\n$placeholders = \'\';\r\nif(\'REX_VALUE[3]\'!=\'\') {\r\n  $ignores = array(\'html\',\'validate\',\'action\');\r\n  $placeholders .= \'\r\n        <div id=\"yform-js-formbuilder-placeholders\">\r\n            <h3>Platzhalter: <span>[<a href=\"#\" id=\"yform-js-formbuilder-placeholders-help-toggler\">?</a>]</span></h3>\'.PHP_EOL;\r\n  foreach($yform->objparams[\'form_elements\'] as $e) {\r\n    if(!in_array($e[0],$ignores) && isset($e[1])) {\r\n      $label = (isset($e[2]) && $e[2] != \'\') ? $e[2] . \': \' : \'\';\r\n      $placeholders .= \'<code>\' . $label . \'###\'.$e[1].\'###</code> \'.PHP_EOL;\r\n    }\r\n  }\r\n  $placeholders .= \'\r\n            <ul id=\"yform-js-formbuilder-placeholders-help\">\r\n                <li>Die Platzhalter ergeben sich aus den obenstehenden Felddefinitionen.</li>\r\n                <li>Per Klick können einzelne Platzhalter in den Mail-Body kopiert werden.</li>\r\n                <li>Aktualisierung der Platzhalter erfolgt über die Aktualisierung des Moduls.</li>\r\n            </ul>\r\n        </div>\'.PHP_EOL;\r\n}\r\n\r\n\r\n// OTHERS\r\n////////////////////////////////////////////////////////////////////////////////\r\n$row_pad = 1;\r\n\r\n$sel = \'REX_VALUE[1]\';\r\n$db_display   = ($sel==\'\' || $sel==1) ?\' style=\"display:none\"\':\'\';\r\n$mail_display = ($sel==\'\' || $sel==0) ?\' style=\"display:none\"\':\'\';\r\n\r\n?>\r\n\r\n<div id=\"yform-formbuilder\">\r\n  <fieldset class=\"form-horizontal\">\r\n    <legend>Formular</legend>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label text-left\">Debug Modus</label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <?= $dbg_sel; ?>\r\n        </div>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\" for=\"yform-formbuilder-definition\">Felddefinitionen</label>\r\n      <div class=\"col-md-10\">\r\n        <textarea class=\"form-control\" style=\"font-family: monospace;\" id=\"yform-formbuilder-definition\" name=\"REX_INPUT_VALUE[3]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[3]\'))+$row_pad);?>\">REX_VALUE[3]</textarea>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Verfügbare Feldklassen</label>\r\n      <div class=\"col-md-10\">\r\n        <div id=\"yform-formbuilder-classes-showhelp\"><?= rex_yform::showHelp(true,true); ?></div>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Meldung bei erfolgreichen Versand</label>\r\n      <div class=\"col-md-10\">\r\n        <label class=\"radio-inline\">\r\n          <input type=\"radio\" name=\"REX_INPUT_VALUE[11]\" value=\"0\"<?php if(\"REX_VALUE[11]\" == \'0\') echo \' checked\'; ?> /> Plaintext\r\n        </label>\r\n        <label class=\"radio-inline\">\r\n          <input type=\"radio\" name=\"REX_INPUT_VALUE[11]\" value=\"1\"<?php if(\"REX_VALUE[11]\" == \'1\') echo \' checked\'; ?> /> HTML\r\n        </label>\r\n        <label class=\"radio-inline\">\r\n          <input type=\"radio\" name=\"REX_INPUT_VALUE[11]\" value=\"2\"<?php if(\"REX_VALUE[11]\" == \'2\') echo \' checked\'; ?> /> Textile\r\n        </label>\r\n      </div>\r\n      <div class=\"col-md-offset-2 col-md-10\">\r\n        <textarea class=\"form-control\" name=\"REX_INPUT_VALUE[6]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[6]\'))+$row_pad);?>\">REX_VALUE[6]</textarea>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\">\r\n    <legend>Vordefinierte Aktionen</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Bei Submit</label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <select class=\"form-control\" id=\"yform-js-formbuilder-action-select\" name=\"REX_INPUT_VALUE[1]\" size=\"1\">\r\n            <option value=\"\"<?php if(\"REX_VALUE[1]\" == \"\")  echo \" selected \"; ?>>Nichts machen (actions im Formular definieren)</option>\r\n            <option value=\"0\"<?php if(\"REX_VALUE[1]\" == \"0\") echo \" selected \"; ?>>Nur in Datenbank speichern</option>\r\n            <option value=\"1\"<?php if(\"REX_VALUE[1]\" == \"1\") echo \" selected \"; ?>>Nur E-Mail versenden</option>\r\n            <option value=\"2\"<?php if(\"REX_VALUE[1]\" == \"2\") echo \" selected \"; ?>>E-Mail versenden und in Datenbank speichern</option>\r\n          </select>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\" id=\"yform-js-formbuilder-mail-fieldset\"<?php echo $mail_display;?> >\r\n    <legend>E-Mail-Versand:</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Absender</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Empfänger</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[12]\" value=\"REX_VALUE[12]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Subject</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[4]\" value=\"REX_VALUE[4]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Mailbody</label>\r\n      <div class=\"col-md-10\">\r\n        <textarea class=\"form-control\" id=\"yform-js-formbuilder-mail-body\" name=\"REX_INPUT_VALUE[5]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[5]\'))+$row_pad);?>\">REX_VALUE[5]</textarea>\r\n        <div class=\"help-block\">\r\n          <?php echo $placeholders;?>\r\n        </div>\r\n      </div>\r\n    </div>\r\n\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\" id=\"yform-js-formbuilder-db-fieldset\"<?php echo $db_display;?> >\r\n    <legend>Datenbank Einstellungen</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Tabelle wählen <span>[<a href=\"#\" id=\"yform-js-formbuilder-db-help-toggler\">?</a>]</span></label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <?= $tbl_sel; ?>\r\n        </div>\r\n        <div class=\"help-block\">\r\n          <ul id=\"yform-js-formbuilder-db-help\">\r\n            <li>Hier werden die Daten des Formular hineingespeichert</li>\r\n          </ul>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n</div>\r\n\r\n<p id=\"yform-formbuilder-info\"><?=  rex_addon::get(\'yform\')->getName() . \' \' . rex_addon::get(\'yform\')->getVersion() ?></p>\r\n\r\n<script type=\"text/javascript\">\r\n  <!--\r\n  (function($){\r\n\r\n    // AUTOGROW BY ROWS\r\n    $(\'#yform-formbuilder textarea\').keyup(function(){\r\n      var rows = $(this).val().split(/\\r?\\n|\\r/).length + <?php echo $row_pad;?>;\r\n      $(this).attr(\'rows\',rows);\r\n    });\r\n\r\n    // TOGGLERS\r\n    $(\'#yform-js-formbuilder-placeholders-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-placeholders-help\').toggle(50);return false;\r\n    });\r\n    $(\'#yform-js-formbuilder-where-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-where-help\').toggle(50);return false;\r\n    });\r\n    $(\'#yform-js-formbuilder-db-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-db-help\').toggle(50);return false;\r\n    });\r\n\r\n\r\n    // INSERT PLACEHOLDERS\r\n    $(\'#yform-js-formbuilder-placeholders code\').click(function(){\r\n      newval = $(\'#yform-js-formbuilder-mail-body\').val()+\' \'+$(this).html();\r\n      $(\'#yform-js-formbuilder-mail-body\').val(newval);\r\n      $(this).addClass(\'text-muted\');\r\n    });\r\n\r\n    // TOGGLE MAIL/DB PANELS\r\n    $(\'#yform-js-formbuilder-action-select\').change(function(){\r\n      switch($(this).val()){\r\n        case \'\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').hide(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').hide(0);\r\n          break;\r\n        case \'1\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').hide(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').show(0);\r\n          break;\r\n        case \'0\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').show(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').hide(0);\r\n          break;\r\n        case \'2\':\r\n        case \'3\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').show(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').show(0);\r\n          break;\r\n      }\r\n    });\r\n\r\n  })(jQuery)\r\n  //-->\r\n</script>','admin','','2016-05-12 08:12:14','0000-00-00 00:00:00','',0),
  (2,'02 . Titel und Untertitel','<?php\r\nif (\"REX_VALUE[7]\" == \'indent\') {\r\n	echo \'\r\n	<div class=\"row\">\r\n	<div class=\"col-md-8 col-md-offset-2 text-center\">\';\r\n}\r\n\r\nif (\"REX_VALUE[4]\" == \'top\') {\r\n	echo \'\r\n	<hr>\';\r\n}\r\n\r\n// Ist Überschrift vorhanden?\r\nif (\"REX_VALUE[1]\" != \'\' || \"REX_VALUE[2]\" != \'\') {\r\n	$align = ($this->getTemplateId() == 1) ? \' class=\"text-center\"\' : \'\';\r\n	echo \'\r\n	<REX_VALUE[3]\'.$align.\'>\';\r\n\r\n		if (\"REX_VALUE[1]\" != \'\') {\r\n			echo \'REX_VALUE[1]\';\r\n		}\r\n\r\n		if (\"REX_VALUE[1]\" != \'\' || \"REX_VALUE[2]\" != \'\') {\r\n			echo \'<br>\';\r\n		}\r\n\r\n		if (\"REX_VALUE[2]\" != \'\') {\r\n			echo \'<small class=\"subtitle\">REX_VALUE[2]</small>\';\r\n		}\r\n\r\n	echo \'</REX_VALUE[3]>\';\r\n}\r\n\r\nif (\"REX_VALUE[4]\" == \'bottom\') {\r\n	echo \'\r\n	<hr>\';\r\n}\r\n\r\nif (\"REX_VALUE[7]\" == \'indent\') {\r\n	echo \'\r\n	</div>\r\n	</div>\';\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nTitel / Intro\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"headline\">Titel</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"subheadline\">Untertitel</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"subheadline\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Größe</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'h1\'=>\'h1\',\r\n				\'h2\'=>\'h2\',\r\n				\'h3\'=>\'h3\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[3]\" id=\"size\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[3]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Trennlinie</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'nein\',\r\n				\'top\'=>\'oberhalb\',\r\n				\'bottom\'=>\'unterhalb\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[4]\" id=\"size\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[4]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Abstand</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keiner\',\r\n				\'top\'=>\'oberhalb\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[5]\" id=\"size\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[5]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Ausrichtung</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'links\',\r\n				\'c\'=>\'zentriert\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[6]\" id=\"size\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[6]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Einzug</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keiner\',\r\n				\'indent\'=>\'links und rechts\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[7]\" id=\"size\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[7]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n		</div>\r\n	</div>\r\n\r\n</fieldset>\r\n','admin','admin','2016-05-12 08:12:46','2016-05-25 20:40:43','',0),
  (3,'03 . Text (Markdown)','<?php\r\n$distance = (\"REX_VALUE[19]\" != \'\') ? \' REX_VALUE[19]\' : \'\';\r\n$align = (\"REX_VALUE[16]\" == \'center\') ? \' text-center\' : \'\';\r\n\r\necho \'\r\n<section class=\"module-wrapper\">\r\n	<div class=\"full\'.$distance.$align.\'\">\';\r\n\r\n		// Linker und rechter Einzug?\r\n		if (\"REX_VALUE[17]\" == \'vd\') {\r\n			echo \'\r\n			<div class=\"row\">\r\n			<div class=\"col-md-8 col-md-offset-2\">\';\r\n		}\r\n\r\n		if (\"REX_VALUE[18]\" == \'top\') {\r\n			echo \'\r\n			<hr>\';\r\n		}\r\n\r\n		if (\"REX_VALUE[1]\" != \'\') {\r\n			echo \'\r\n			<REX_VALUE[2]>REX_VALUE[1]</REX_VALUE[2]>\';\r\n		}\r\n\r\n		if (\'REX_VALUE[id=3 isset=1]\') {\r\n			// Der Text wird durch Markdown geparst\r\n			$markdown = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=3 output=\"html\"]\');\r\n			echo str_replace(array(\'<strong><em>\',\'</em></strong>\'), array(\'<span class=\"label label-primary\">\',\'</span>\'), $markdown);\r\n		}\r\n\r\n		$link = \'\';\r\n		if (\"REX_LINK[1]\" != \'\') {\r\n			$article = rex_article::get(\"REX_LINK[1]\");\r\n			$linktext = $article->getValue(\'name\');\r\n			$linktext = (\"REX_VALUE[11]\" != \'\') ? \'REX_VALUE[11]\' : $linktext;\r\n			$link = \'\r\n			<br><p><a href=\"\'.rex_getUrl(\"REX_LINK[1]\").\'\" class=\"btn\">\'.$linktext.\'</a></p>\';\r\n		}\r\n		if (\"REX_VALUE[10]\" != \'\') {\r\n			$linktext = (\"REX_VALUE[11]\" != \'\') ? \'REX_VALUE[11]\' : \'REX_VALUE[10]\';\r\n			$link = \'\r\n			<br><p><a href=\"REX_VALUE[10]\" class=\"btn\">\'.$linktext.\'</a></p>\';			\r\n		}\r\n		echo $link;\r\n\r\n		if (\"REX_VALUE[18]\" == \'bottom\') {\r\n			echo \'\r\n			<hr>\';\r\n		}\r\n\r\n		if (\"REX_VALUE[17]\" == \'vd\') {\r\n		echo \'\r\n			</div>\r\n			</div>\';\r\n		}\r\n		?>\r\n	</div>\r\n</section>\r\n','<!-- *******************************************************\r\nTEXT\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Text</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline-level\">Überschrift-Ebene</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'h2\'=>\'2. Ebene\',\r\n				\'h3\'=>\'3. Ebene\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[2]\" id=\"headline-level\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[2]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"markitup_markdown_1\">Text</label>\r\n        <div class=\"col-sm-10\">\r\n            <textarea cols=\"1\" rows=\"6\" class=\"form-control markitupEditor-markdown_full\" id=\"markitup_markdown_1\" name=\"REX_INPUT_VALUE[3]\">REX_VALUE[3]</textarea>\r\n        </div>\r\n    </div>\r\n</fieldset>\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Button-Link</legend>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\">Interner Link</label>\r\n		<div class=\"col-sm-10\">\r\n			REX_LINK[id=\"1\" widget=\"1\"]\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Oder externer Link</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[10]\" value=\"REX_VALUE[10]\" />\r\n        </div>\r\n    </div>\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Individueller Linktext</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[11]\" value=\"REX_VALUE[11]\" />\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Formatierung</legend>\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"padding\">Einrückung</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keine\',\r\n				\'vd\'=>\'ja\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[17]\" id=\"padding\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[17]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n\r\n			<br><section class=\"rex-page-section\">\r\n			    <div class=\"panel panel-default\">\r\n\r\n			        <header class=\"panel-heading collapsed\" data-toggle=\"collapse\" data-target=\"#collapse-REX_SLICE_ID\" aria-expanded=\"false\">\r\n						<div class=\"panel-title\"><i class=\"rex-icon rex-icon-info\"></i> Hinweis</div>\r\n					</header>\r\n\r\n			        <div id=\"collapse-REX_SLICE_ID\" class=\"panel-collapse collapse\" aria-expanded=\"false\" style=\"height: 0px;\">\r\n\r\n			            <div class=\"panel-body\" style=\"background: #f3f6fb;\">\r\n			                <ul>\r\n								<li>Hier kann man eine linke und rechte Einrückung von je zwei Spalten des 12er-Bootstrap-Rasters aktivieren.</li>\r\n								<li>Der vertikale Abstand erzeugt einen oberen und unteren Zwischenraum von jeweils 60px.</li>\r\n							</ul>\r\n						</div>\r\n			        </div>\r\n			    </div>\r\n			</section>\r\n\r\n		</div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"distance\">vertikaler Abstand</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'keiner\',\r\n				\'mt\'=>\'oberhalb\',\r\n				\'mb\'=>\'unterhalb\',\r\n				\'mtb\'=>\'ober- und unterhalb\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[19]\" id=\"distance\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[19]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"line\">Ausrichtung</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'normal\',\r\n				\'center\'=>\'zentriert\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[16]\" id=\"line\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[16]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"line\">Trennlinie</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'nein\',\r\n				\'top\'=>\'oberhalb\',\r\n				\'bottom\'=>\'unterhalb\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[18]\" id=\"line\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[18]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2016-05-12 08:13:12','2016-05-27 12:16:54','',0),
  (4,'10 . Googlemap','<?php\r\n$marker = \'\';\r\n$info_window = \'\';\r\n\r\n$sql = rex_sql::factory();\r\n$sql_query = \'\r\n	SELECT name, color, link, lat, lng FROM rex_locations\';\r\n\r\n$sql->setQuery($sql_query);\r\n\r\nfor ($i=0; $i<$sql->getRows(); $i++) {\r\n\r\n	$marker .= \"\r\n	[\'\".htmlspecialchars($sql->getValue(\'name\'), ENT_QUOTES).\"\', \'/resources/css/files/\".$sql->getValue(\'color\').\".png\', \".$sql->getValue(\'lat\').\",\".$sql->getValue(\'lng\').\"]\";\r\n	if ($i+1 < $sql->getRows()) $marker .= \',\';\r\n\r\n	$info_window .= \"\r\n        [\'<div class=\\\"info_content\\\">\' +\r\n		\'<a href=\\\"\".$sql->getValue(\'link\').\"\\\">\' +\r\n		\'<h4>\".htmlspecialchars($sql->getValue(\'name\'), ENT_QUOTES).\"</h4>\' +\r\n        \'</a>\' +\'</div>\']\";\r\n		if ($i+1 < $sql->getRows()) $info_window .= \',\';\r\n	\r\n	$sql->next();\r\n}\r\n?>\r\n<script>\r\njQuery(function($) {\r\n	\"use strict\";\r\n    // Asynchronously Load the map API \r\n    var script = document.createElement(\'script\');\r\n    script.src = \"http://maps.googleapis.com/maps/api/js?sensor=false&callback=initialize\";\r\n    document.body.appendChild(script);\r\n});\r\n\r\nfunction initialize() {\r\n    var map;\r\n    var bounds = new google.maps.LatLngBounds();\r\n    var mapOptions = {\r\n        mapTypeId: \'roadmap\',\r\n		scrollwheel: false,\r\n		styles: [{\r\n			stylers: [{\r\n				saturation: -REX_VALUE[2]\r\n			}]\r\n		}]\r\n    };\r\n	\r\n                    \r\n    // Display map\r\n    map = new google.maps.Map(document.getElementById(\"map_canvas\"), mapOptions);\r\n    map.setTilt(45);\r\n        \r\n    // Multiple Markers\r\n    var markers = [<?php echo $marker; ?>];\r\n\r\n    // Info Window Content\r\n    var infoWindowContent = [<?php echo $info_window; ?>];\r\n\r\n    // Display multiple markers\r\n    var infoWindow = new google.maps.InfoWindow(), marker, i;\r\n    \r\n    // Loop through array of markers\r\n    for( i = 0; i < markers.length; i++ ) {\r\n        var position = new google.maps.LatLng(markers[i][2], markers[i][3]);\r\n        bounds.extend(position);\r\n        marker = new google.maps.Marker({\r\n            position: position,\r\n            map: map,\r\n            icon: markers[i][1],\r\n            title: markers[i][0]\r\n        });\r\n        \r\n        // Info window for each marker\r\n        google.maps.event.addListener(marker, \'click\', (function(marker, i) {\r\n            return function() {\r\n                infoWindow.setContent(infoWindowContent[i][0]);\r\n                infoWindow.open(map, marker);\r\n            }\r\n        })(marker, i));\r\n\r\n        // Center the map fitting all markers on the screen\r\n        map.fitBounds(bounds);\r\n    }\r\n\r\n    // Override map zoom level once our fitBounds function runs (Make sure it only runs once)\r\n    var boundsListener = google.maps.event.addListener((map), \'bounds_changed\', function(event) {\r\n        this.setZoom(REX_VALUE[1]);\r\n        google.maps.event.removeListener(boundsListener);\r\n    });\r\n    \r\n}\r\n</script>\r\n\r\n</div>\r\n<div id=\"map_wrapper\">\r\n	<div id=\"map_canvas\">\r\n	</div>\r\n</div>\r\n<div class=\"container\">','<!-- *******************************************************\r\nGOOGLEMAP\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Googlemap</legend>\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"shadow\">Zoomfaktor</label>\r\n        <div class=\"col-sm-10\">\r\n			<select name=\"REX_INPUT_VALUE[1]\" id=\"shadow\" class=\"form-control\">\r\n			<?php\r\n			for ($i=1; $i<15; $i++) {\r\n				echo \'<option value=\"\'.$i.\'\" \';\r\n				if (\"REX_VALUE[1]\"==$i) {\r\n					echo \'selected=\"selected\" \';\r\n				}\r\n				echo \'>\'.$i.\'</option>\';\r\n			}\r\n			?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"shadow\">Sättigung reduzieren</label>\r\n        <div class=\"col-sm-10\">\r\n			<select name=\"REX_INPUT_VALUE[2]\" id=\"shadow\" class=\"form-control\">\r\n			<?php\r\n			for ($j=0; $j<=100; $j=$j+10) {\r\n				echo \'<option value=\"\'.$j.\'\" \';\r\n				if (\"REX_VALUE[2]\"==$j) {\r\n					echo \'selected=\"selected\" \';\r\n				}\r\n				echo \'>\'.$j.\'%</option>\';\r\n			}\r\n			?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n</fieldset>\r\n','admin','admin','2016-05-12 08:13:41','2016-05-30 00:23:20','',0),
  (5,'05 . Farbige Kästen','<?php\r\n$visible = true; // Zunächst für jeden sichtbar\r\nif (\"REX_VALUE[18]\" == 1) {\r\n	if (!rex_backend_login::hasSession()) {	// wenn man nicht in Redaxo eingeloggt ist, Status auf unsichtbar\r\n		$visible = false;\r\n	}\r\n}\r\n\r\nif ($visible == true) {\r\n	\r\n	$grid_content1 = \'\';\r\n	$grid_content2 = \'\';\r\n	$grid_content3 = \'\';\r\n\r\n	// col1\r\n	if (\'REX_VALUE[id=1 isset=1]\') {\r\n	    $grid_content1 .= \'<h6 class=\"category\">REX_VALUE[1]</h6>\';\r\n	}\r\n	if (\'REX_VALUE[id=2 isset=1]\') {\r\n	    $grid_content1 .= \'<h4 class=\"title\">REX_VALUE[2]</h4>\';\r\n	}\r\n	if (\'REX_VALUE[id=3 isset=1]\') {\r\n	    $grid_content1 .= rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=3 output=\"html\"]\');\r\n	}\r\n	if (\"REX_LINK[1]\" != \'\') {\r\n		$link = rex_article::get(\"REX_LINK[1]\");\r\n		$link_name = $link->getValue(\'name\');\r\n		$grid_content1 .= \'\r\n		<div class=\"card-footer btn-center\">\r\n			<a href=\"\'.rex_getUrl(\"REX_LINK[1]\").\'\" class=\"btn btn-default btn-round btn-fill btn-primary\">\'.$link_name.\'</a>\r\n		</div>\';\r\n	}\r\n\r\n	$color1 = (\"REX_VALUE[5]\" != \'\') ? \' data-background=\"color\" data-color=\"REX_VALUE[5]\"\' : \'\';\r\n	if (\"REX_VALUE[4]\" == \'shadow\') {\r\n		$wrapper_start1 = \'\r\n		<div class=\"card card-with-shadow\"\'.$color1.\'>\r\n		<div class=\"content\">\';\r\n		$wrapper_end1 = \'\r\n		</div>\r\n		</div>\';\r\n	} elseif (\"REX_VALUE[4]\" == \'big-shadow\') {\r\n		$wrapper_start1 = \'\r\n		<div class=\"card-big-shadow\">\r\n		<div class=\"card\"\'.$color1.\'>\r\n		<div class=\"content\">\';\r\n		$wrapper_end1 = \'\r\n		</div>\r\n		</div>\r\n		</div>\';\r\n	} else {\r\n		$wrapper_start1 = \'\r\n		<div class=\"card\"\'.$color1.\'>\r\n		<div class=\"content\">\';\r\n		$wrapper_end1 = \'\r\n		</div>\r\n		</div>\';\r\n	}\r\n\r\n	// col2\r\n	if (\'REX_VALUE[id=6 isset=1]\') {\r\n	    $grid_content2 .= \'<h6 class=\"category\">REX_VALUE[6]</h6>\';\r\n	}\r\n	if