<!-- *******************************************************\r\nTEXT UND BILD IN SPALTEN\r\n******************************************************** -->\r\n\r\n<script type=\"text/javascript\">\r\njQuery(function($){\r\n	$(document).ready(function(){\r\n\r\n		$(\'select#choices\').change(function() {\r\n			$(\'.off\').hide();\r\n\r\n			for(var i = 1; i <= parseInt($(this).val()); i++) {\r\n				$(\'#col\' + i).show();\r\n			}\r\n		});\r\n\r\n		$(\'select#choices\').change();\r\n\r\n	})\r\n});\r\n</script>\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"only_admin\">Sichtbar</label>\r\n        <div class=\"col-sm-10\">\r\n			<input type=\"checkbox\" name=\"REX_INPUT_VALUE[18]\" id=\"only_admin\" value=\"1\" <?= \"REX_VALUE[18]\" == \'1\' ? \' checked=\"checked\"\' : \'\' ?>> Nur, wenn in Redaxo eingeloggt\r\n        </div>\r\n    </div>\r\n</fieldset>\r\n\r\n<fieldset class=\"form-horizontal\">\r\n	<legend>Spalten und Abstände</legend>\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"choices\">Breiten</label>\r\n        <div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'1\'=>\'volle Breite\',\r\n				\'2\'=>\'zwei Spalten\',\r\n				\'3\'=>\'drei Spalten\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[19]\" id=\"choices\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[19]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Abstand</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'\'=>\'oben und unten\',\r\n				\'top\'=>\'nur oben\',\r\n				\'bottom\'=>\'nur unten\',\r\n				\'no-distance\' => \'none\'\r\n			);\r\n