  (6,'06 . Neueste User','<div class=\"row\">\r\n\r\n<h2 class=\"text-center\">Die neuesten User</h2><br>\r\n	<?php\r\n	$sql = rex_sql::factory();\r\n	$sql_query = \'\r\n		SELECT\r\n			firstname, name, pic, slogan, email\r\n		FROM\r\n			rex_ycom_user\r\n		WHERE\r\n			status = 1\r\n		ORDER BY\r\n			id DESC\r\n		LIMIT 4\';\r\n\r\n	$sql->setQuery($sql_query);\r\n\r\n	for ($i=0; $i<$sql->getRows(); $i++) {\r\n\r\n		$profile_pic = \'profile_default.png\';\r\n		if ($sql->getValue(\'pic\') != \'\') $profile_pic = $sql->getValue(\'pic\');\r\n\r\n		echo \'\r\n		<div class=\"col-md-3 col-sm-6\">\r\n			<div class=\"card card-user\">\r\n				<div class=\"image\">\r\n					 <img src=\"\'.rex_url::base(\'index.php?rex_media_type=blur&rex_media_file=\'.$profile_pic.\'\').\'\" alt=\"\'.$sql->getValue(\'firstname\').\' \'.$sql->getValue(\'name\').\'\"/>\r\n				</div>\r\n				<div class=\"content\">\r\n					<div class=\"author\">\r\n						<img class=\"avatar border-white\" src=\"\'.rex_url::base(\'index.php?rex_media_type=profile&rex_media_file=\'.$profile_pic.\'\').\'\" alt=\"\'.$sql->getValue(\'firstname\').\' \'.$sql->getValue(\'name\').\'\"/>\r\n\r\n						  <h4 class=\"title\">\'.$sql->getValue(\'firstname\').\' \'.$sql->getValue(\'name\').\'</h4>\r\n					</div>\r\n					<p class=\"description text-center\">\r\n						\'.$sql->getValue(\'slogan\').\'\r\n					</p>\r\n				</div>\r\n				<div class=\"card-footer text-center\">\r\n					<button href=\"mailto:\'.$sql->getValue(\'slogan\').\'\" class=\"btn btn-icon\"><i class=\"ti-email\"></i></button>\r\n				</div>\r\n			</div> <!-- end card -->\r\n		</div>\';\r\n\r\n		$sql->next();\r\n	}\r\n	?>\r\n\r\n</div>','','admin','admin','2016-05-12 09:48:10','2016-06-02 21:48:39','',0),
  (7,'07 . Tabs','<?php\r\n$tab_title1 = (\"REX_VALUE[1]\" != \'\') ? \'<li class=\"active\"><a href=\"#tab1\" data-toggle=\"tab\">REX_VALUE[1]</a></li>\' : \'\';\r\n$tab_title2 = (\"REX_VALUE[2]\" != \'\') ? \'<li><a href=\"#tab2\" data-toggle=\"tab\">REX_VALUE[2]</a></li>\' : \'\';\r\n$tab_title3 = (\"REX_VALUE[3]\" != \'\') ? \'<li><a href=\"#tab3\" data-toggle=\"tab\">REX_VALUE[3]</a></li>\' : \'\';\r\n$tab_title4 = (\"REX_VALUE[4]\" != \'\') ? \'<li><a href=\"#tab4\" data-toggle=\"tab\">REX_VALUE[4]</a></li>\' : \'\';\r\n$tab_title5 = (\"REX_VALUE[5]\" != \'\') ? \'<li><a href=\"#tab5\" data-toggle=\"tab\">REX_VALUE[5]</a></li>\' : \'\';\r\n\r\n$markdown1 = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=11 output=\"html\"]\');\r\n$markdown2 = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=12 output=\"html\"]\');\r\n$markdown3 = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=13 output=\"html\"]\');\r\n$markdown4 = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=14 output=\"html\"]\');\r\n$markdown5 = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=15 output=\"html\"]\');\r\n\r\n$tab_text1 = (\"REX_VALUE[11]\" != \'\') ? \'<div class=\"tab-pane active\" id=\"tab1\">\'.str_replace(array(\'<strong><em>\',\'</em></strong>\'), array(\'<span class=\"label label-primary\">\',\'</span>\'), $markdown1).\'</div>\' : \'\';\r\n$tab_text2 = (\"REX_VALUE[12]\" != \'\') ? \'<div class=\"tab-pane\" id=\"tab2\">\'.str_replace(array(\'<strong><em>\',\'</em></strong>\'), array(\'<span class=\"label label-primary\">\',\'</span>\'), $markdown2).\'</div>\' : \'\';\r\n$tab_text3 = (\"REX_VALUE[13]\" != \'\') ? \'<div class=\"tab-pane\" id=\"tab3\">\'.str_replace(array(\'<strong><em>\',\'</em></strong>\'), array(\'<span class=\"label label-primary\">\',\'</span>\'), $markdown3).\'</div>\' : \'\';\r\n$tab_text4 = (\"REX_VALUE[14]\" != \'\') ? \'<div class=\"tab-pane\" id=\"tab4\">\'.str_replace(array(\'<strong><em>\',\'</em></strong>\'), array(\'<sp