  (8,'08 . Button mit Modal-Popup','<?php\r\n$visible = true; // Zunächst für jeden sichtbar\r\nif (\"REX_VALUE[18]\" == 1) { // definiert als nur sichtbar, wenn eingeloggt\r\n	if (!rex_backend_login::hasSession()) {	// wenn man nicht in Redaxo eingeloggt ist, Status auf unsichtbar\r\n		$visible = false;\r\n	}\r\n}\r\n\r\nif ($visible == true) {\r\n\r\n	echo \'\r\n	<div id=\"modals\">\r\n	    <button class=\"btn modal-opener\" data-toggle=\"modal\" data-target=\"#classicModal\">\r\n	              <i class=\"ti-announcement\"></i> REX_VALUE[1]\r\n	    </button>\r\n	</div>\r\n\r\n	<!-- classic modal -->\r\n	<div class=\"modal fade\" id=\"classicModal\" tabindex=\"-1\" role=\"dialog\" aria-labelledby=\"myModalLabel\" aria-hidden=\"true\">\r\n		<div class=\"modal-dialog\">\r\n			<div class=\"modal-content\">\';\r\n\r\n				if (\"REX_VALUE[2]\" != \'\') {\r\n					echo \'\r\n					<div class=\"modal-header\">\r\n						<button type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-hidden=\"true\">&times;</button>\r\n						<h4 class=\"modal-title\" id=\"myModalLabel\">REX_VALUE[2]</h4>\r\n					</div>\';\r\n				}\r\n				\r\n				echo \'\r\n				<div class=\"modal-body\">\';\r\n\r\n				if (\'REX_VALUE[id=3 isset=1]\') {\r\n					$markdown = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=3 output=\"html\"]\');\r\n					echo str_replace(array(\'<strong><em>\',\'</em></strong>\'), array(\'<span class=\"label label-primary\">\',\'</span>\'), $markdown);\r\n				}\r\n\r\n				echo \'\r\n				</div>\r\n\r\n				<div class=\"modal-footer\">\';\r\n\r\n					if (\"REX_VALUE[4]\" != \'\') {\r\n						echo \'\r\n						<div class=\"left-side\">\r\n							<button type=\"button\" class=\"btn btn-default btn-simple\">REX_VALUE[4]</button>\r\n						</div>\r\n						<div class=\"divider\"></div>\';\r\n\r\n					}\r\n\r\n					$class = (\"REX_VALUE[4]\" != \'\') ? \' class=\"right-side\"\' : \'\';\r\n					echo \'\r\n					<div\'.$class.\'>\r\n						<button type=\"button\" class=\"btn btn-danger btn-simple\" data-dismiss=\"modal\">Schließen</button>\r\n					</div>\r\n\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n	<!-- end modal -->\';\r\n\r\n}\r\n?>','<!-- *******************************************************\r\nBUTTON MIT MODUL-POPUP\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"only_admin\">Sichtbar</label>\r\n        <div class=\"col-sm-10\">\r\n			<input type=\"checkbox\" name=\"REX_INPUT_VALUE[18]\" id=\"only_admin\" value=\"1\" <?= \"REX_VALUE[18]\" == \'1\' ? \' checked=\"checked\"\' : \'\' ?>> Nur, wenn in Redaxo eingeloggt\r\n\r\n			<br><br>\r\n			<section class=\"rex-page-section\">\r\n				<div class=\"panel panel-default\">\r\n\r\n					<header class=\"panel-heading collapsed\" data-toggle=\"collapse\" data-target=\"#collapse-REX_SLICE_ID\" aria-expanded=\"false\">\r\n						<div class=\"panel-title\"><i class=\"rex-icon rex-icon-info\"></i> Hinweis</div>\r\n					</header>\r\n\r\n					<div id=\"collapse-REX_SLICE_ID\" class=\"panel-collapse collapse\" aria-expanded=\"false\" style=\"height: 0px;\">\r\n\r\n						<div class=\"panel-body\" style=\"background: #f3f6fb;\">\r\n							<ul>\r\n								<li>Hier kann man eine linke und rechte Einrückung von je zwei Spalten des 12er-Bootstrap-Rasters aktivieren.</li>\r\n								<li>Der vertikale Abstand erzeugt einen oberen und unteren Zwischenraum von jeweils 60px.</li>\r\n							</ul>\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</section>\r\n\r\n		</div>\r\n	</div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"button-text\">Button-Text</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"button-text\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"markitup_markdown_1\">Text</label>\r\n        <div class=\"col-sm-10\">\r\n            <textarea cols=\"1\" rows=\"6\" class=\"form-control markitupEditor-markdown_full\" id=\"markitup_markdown_1\" name=\"REX_INPUT_VALUE[3]\">REX_VALUE[3]</textarea>\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','admin','2016-05-12 09:49:02','2016-05-30 12:47:00','',0),
  (9,'09 . Galerie','<?php\r\nif (\"REX_MEDIALIST[1]\" != \'\') {\r\n\r\n	$pics_per_row = \'\';\r\n	switch (\"REX_VALUE[1]\") {\r\n	case \'2\':\r\n	   $pics_per_row = \'col-md-6 col-sm-12\';\r\n	   break;\r\n\r\n	case \'3\':\r\n	   $pics_per_row = \'col-md-4 col-sm-12\';\r\n	   break;\r\n\r\n	case \'4\':\r\n	   $pics_per_row = \'col-md-3 col-sm-6\';\r\n	   break;\r\n	}\r\n	\r\n	$imagelist = explode(\',\', \"REX_MEDIALIST[1]\");\r\n	\r\n	echo \'\r\n	<!-- gallery -->\r\n	<!-- Root element von PhotoSwipe. Muss die Klasse pswp haben. -->\r\n	<div class=\"pswp\" tabindex=\"-1\" role=\"dialog\" aria-hidden=\"true\">\r\n	\r\n		<!-- Hintergrund von PhotoSwipe.\r\n		Ein separates Element, das Animieren von opacity schneller ist als rgba(). -->\r\n		<div class=\"pswp__bg\"></div>\r\n	\r\n		<!-- Slides wrapper with overflow:hidden. -->\r\n		<div class=\"pswp__scroll-wrap\">\r\n	\r\n			<!-- Container that holds slides.\r\n			PhotoSwipe hält nur 3 im DOM, um Speicher zu sparen.\r\n			Nicht diese 3 pswp__item Elemente verändern, weil die Daten später modifiziert werden. -->\r\n			<div class=\"pswp__container\">\r\n				<div class=\"pswp__item\"></div>\r\n				<div class=\"pswp__item\"></div>\r\n				<div class=\"pswp__item\"></div>\r\n			</div>\r\n	\r\n			<!-- Default (PhotoSwipeUI_Default) interface on top of sliding area. Can be changed. -->\r\n			<div class=\"pswp__ui pswp__ui--hidden\">\r\n	\r\n				<div class=\"pswp__top-bar\">\r\n	\r\n					<!--  Controls are self-explanatory. Order can be changed. -->\r\n					<div class=\"pswp__counter\"></div>\r\n	\r\n					<button class=\"pswp__button pswp__button--close\" title=\"Schließen (Esc)\"></button>\r\n					<button class=\"pswp__button pswp__button--share\" title=\"Teilen\"></button>\r\n					<button class=\"pswp__button pswp__button--fs\" title=\"Vollbild\"></button>\r\n					<button class=\"pswp__button pswp__button--zoom\" title=\"Vergrößern / Verkleinern\"></button>\r\n	\r\n					<!-- Preloader demo http://codepen.io/dimsemenov/pen/yyBWoR -->\r\n					<!-- element will get class pswp__preloader--active when preloader is running -->\r\n					<div class=\"pswp__preloader\">\r\n						<div class=\"pswp__preloader__icn\">\r\n						  <div class=\"pswp__preloader__cut\">\r\n							<div class=\"pswp__preloader__donut\"></div>\r\n						  </div>\r\n						</div>\r\n					</div>\r\n				</div>\r\n	\r\n				<div class=\"pswp__share-modal pswp__share-modal--hidden pswp__single-tap\">\r\n					<div class=\"pswp__share-tooltip\"></div>\r\n				</div>\r\n	\r\n				<button class=\"pswp__button pswp__button--arrow--left\" title=\"Zurück (Pfeil links)\"></button>\r\n				<button class=\"pswp__button pswp__button--arrow--right\" title=\"Weiter (Pfeil rechts)\"></button>\r\n	\r\n				<div class=\"pswp__caption\">\r\n					<div class=\"pswp__caption__center\"></div>\r\n				</div>\r\n	\r\n			</div>\r\n		</div>\r\n	</div>\r\n	\r\n	<div class=\"my-gallery\" itemscope itemtype=\"http://schema.org/ImageGallery\">\r\n		<div class=\"row\">\';\r\n	\r\n			foreach ($imagelist as $file) {\r\n				$media = rex_media::get($file);\r\n				if ($media) {\r\n				\r\n					$mediatitle = $media->getValue(\'title\');\r\n					$mediadesc = str_replace(array(\"\\r\\n\", \"\\n\", \"\\r\"), \' \', $media->getValue(\'med_description\'));\r\n\r\n					$pic = rex_path::media($file);\r\n					$size = getimagesize($pic);\r\n					$width = $size[0];\r\n					$height = $size[1];\r\n								\r\n					echo \'\r\n					<figure itemprop=\"associatedMedia\" itemscope itemtype=\"http://schema.org/ImageObject\" class=\"\'.$pics_per_row.\' gallery-item\">\r\n						<a href=\"\'.rex_url::base(\'media/\'.$file).\'\" itemprop=\"contentUrl\" data-size=\"\'.$width.\'x\'.$height.\'\">\r\n							<img src=\"\'.rex_url::base(\'index.php?rex_media_type=thumb&rex_media_file=\'.$file.\'\').\'\" itemprop=\"thumbnail\" alt=\"\'.$mediadesc.\'\" class=\"img-rounded img-responsive\" />\r\n						</a>\r\n						<figcaption itemprop=\"caption description\" class=\"gallery-caption\">\'.$mediatitle.\'</figcaption>\r\n					</figure>\';\r\n\r\n				}\r\n			}\r\n\r\n		echo \'\r\n		</div>\r\n	</div>\r\n	<div class=\"clearfix\"></div>\r\n	<!-- end gallery -->\';\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nGALERIE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Galerie</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Bilder</label>\r\n		<div class=\"col-sm-10\">\r\n            REX_MEDIALIST[id=\"1\" widget=\"1\"]\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Bilder pro Zeile</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'2\'=>\'2\',\r\n				\'3\'=>\'3\',\r\n				\'4\'=>\'4\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[1]\" id=\"size\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[1]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n		</div>\r\n	</div>\r\n</fieldset>\r\n','admin','admin','2016-05-12 09:49:25','2016-06-02 21:52:41','',0),
  (10,'14 . Text Seitenspalte','<div class=\"contact-info\">\r\n	<?php\r\n	if (\"REX_VALUE[1]\" != \'\') {\r\n		$icon = (\"REX_VALUE[2]\" != \'\') ? \'<i class=\"fa REX_VALUE[2] text-muted\"></i> \' : \'\';\r\n		echo \'\r\n		<h5>\'.$icon.\'REX_VALUE[1]</h5>\';\r\n	}\r\n\r\n	if (\'REX_VALUE[id=3 isset=1]\') {\r\n		$markdown = rex_markitup::parseOutput(\'markdown\', \'REX_VALUE[id=3 output=\"html\"]\');\r\n		echo str_replace(\'<p>\', \'<p class=\"text-muted\">\', $markdown);\r\n	}\r\n	?>\r\n</div>','<!-- *******************************************************\r\nTEXT SEITENSPALTE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Text Seitenspalte</legend>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"icon\">Icon</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"icon\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 markitup_markdown_1\" for=\"markitup_3\">Text</label>\r\n		<div class=\"col-sm-10\">\r\n			<textarea cols=\"1\" rows=\"6\" class=\"form-control markitupEditor-markdown_full\" id=\"markitup_markdown_1\" name=\"REX_INPUT_VALUE[3]\">REX_VALUE[3]</textarea>\r\n		</div>\r\n	</div>\r\n\r\n</fieldset>\r\n','admin','admin','2016-05-12 09:49:54','2016-05-25 12:15:09','',0),
  (11,'15 . Footer - Links','<?php\r\nif (\'REX_LINKLIST[1]\' != \'\') {\r\n	echo \'<ul>\';\r\n	$linklist = explode(\',\',\'REX_LINKLIST[1]\');\r\n	foreach ($linklist as $link) {\r\n		$art = rex_article::get($link);\r\n		if (is_object($art)) {\r\n			$art_name = $art->getValue(\'name\');\r\n			echo \'\r\n			<li><a href=\"\'.rex_getUrl($link).\'\">\'.$art_name.\'</a></li>\';\r\n		}\r\n	}\r\n	echo \'</ul>\';\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nFOOTER LINKLISTE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Text</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n        <div class=\"col-sm-10\">\r\n            <input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\">Links</label>\r\n        <div class=\"col-sm-10\">\r\n            REX_LINKLIST[id=\"1\" widget=\"1\"]\r\n        </div>\r\n    </div>\r\n\r\n</fieldset>\r\n','admin','','2016-05-12 09:50:16','0000-00-00 00:00:00','',0),
  (12,'12 . Trennermodul','<?php\r\n$background = (\"REX_MEDIA[1]\" != \'\') ? \' style=\"background-image: url(index.php?rex_media_type=seperator&rex_media_file=REX_MEDIA[1]);\"\' : \'\';\r\n?>\r\n</div>\r\n<div class=\"header-wrapper\">\r\n	<div class=\"header\"<?php echo $background; ?>>\r\n		<div class=\"filter\"></div>\r\n		<div class=\"container text-center\">\r\n			REX_VALUE[id=\'1\' prefix=\'<h1>\' suffix=\'</h1>\']\r\n			REX_VALUE[id=\'2\' prefix=\'<h3>\' suffix=\'</h3>\']\r\n		</div>\r\n	</div>\r\n</div>\r\n<div class=\"container\">','<!-- *******************************************************\r\nTRENNERMODUL\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Trennermodul</legend>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\">Hintergrund-Bild</label>\r\n		<div class=\"col-sm-10\">\r\n			REX_MEDIA[id=\"1\" widget=\"1\"]\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n		</div>\r\n	</div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"sub-headline\">Unter-Überschrift</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"sub-headline\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n		</div>\r\n	</div>\r\n\r\n</fieldset>\r\n','admin','admin','2016-05-13 10:22:55','2016-05-30 13:05:31','',0),
  (13,'04 . großes Bild','<?php\r\nif (\"REX_MEDIA[1]\" != \'\') {\r\n\r\n	$style = \'\';\r\n	switch (\"REX_VALUE[1]\") {\r\n	case \'shadow\':\r\n	   $style = \' card-with-shadow\';\r\n	   break;\r\n\r\n	case \'big-shadow\':\r\n   	   $style = \' card-big-shadow\';\r\n   	   break;\r\n	}\r\n\r\n	echo \'\r\n	<div class=\"card\'.$style.\' full-card\">\r\n		<div class=\"card\" data-radius=\"none\">\r\n			<img src=\"\'.rex_url::base(\'index.php?rex_media_type=big&rex_media_file=REX_MEDIA[1]\').\'\">                                                                                    \r\n		</div>\r\n	</div>\';\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nGROSSES BILD\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Großes Bild</legend>\r\n		<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\">Bild</label>\r\n		<div class=\"col-sm-10\">\r\n			REX_MEDIA[id=\"1\" widget=\"1\"]\r\n		</div>\r\n	</div>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"shadow\">Schatten</label>\r\n        <div class=\"col-sm-10\">\r\n            <?php\r\n                $options = array(\r\n                \'\'=>\'normal\',\r\n                \'shadow\'=>\'dezenter Schatten\',\r\n                \'big-shadow\'=>\'großer Schatten\'\r\n            );\r\n            ?>\r\n            <select name=\"REX_INPUT_VALUE[1]\" id=\"shadow\" class=\"form-control\">\r\n            <?php foreach ($options as $k=>$v) : ?>\r\n            <option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[1]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n            <?php endforeach ?>\r\n            </select>\r\n        </div>\r\n    </div>\r\n</fieldset>\r\n','admin','admin','2016-05-23 07:37:44','2016-06-02 21:18:34','',0),
  (14,'11 . Social Links','<div class=\"col-md-6 col-md-offset-3 text-center social\">\r\n\r\n	REX_VALUE[id=\'1\' prefix=\'<h3><small>\' suffix=\'</small></h3>\']\r\n\r\n	<?php\r\n	if (\"REX_VALUE[2]\" != \'\') {\r\n		echo \'\r\n		<a class=\"btn btn-icon btn-fill btn-twitter\" href=\"REX_VALUE[2]\">\r\n			<i class=\"fa fa-twitter\"></i>\r\n		</a>\';\r\n	}\r\n\r\n	if (\"REX_VALUE[3]\" != \'\') {\r\n		echo \'\r\n		<a class=\"btn btn-icon btn-fill btn-facebook\" href=\"REX_VALUE[3]\">\r\n			<i class=\"fa fa-facebook\"></i>\r\n		</a>\';\r\n	}\r\n\r\n	if (\"REX_VALUE[4]\" != \'\') {\r\n		echo \'\r\n		<a class=\"btn btn-icon btn-fill btn-google\" href=\"REX_VALUE[4]\">\r\n			<i class=\"fa fa-google\"></i>\r\n		</a>\';\r\n	}\r\n\r\n	if (\"REX_VALUE[5]\" != \'\') {\r\n		echo \'\r\n		<a class=\"btn btn-icon btn-fill btn-dribbble\" href=\"REX_VALUE[5]\">\r\n			<i class=\"fa fa-dribbble\"></i>\r\n		</a>\';\r\n	}\r\n\r\n	if (\"REX_VALUE[6]\" != \'\') {\r\n		echo \'\r\n		<a class=\"btn btn-icon btn-fill btn-instagram\" href=\"REX_VALUE[6]\">\r\n			<i class=\"fa fa-instagram\"></i>\r\n		</a>\';\r\n	}\r\n\r\n	if (\"REX_VALUE[7]\" != \'\') {\r\n		echo \'\r\n		<a class=\"btn btn-icon btn-fill btn-youtube\" href=\"REX_VALUE[7]\">\r\n			<i class=\"fa fa-youtube\"></i>\r\n		</a>\';\r\n	}\r\n	?>\r\n</div>','<!-- *******************************************************\r\nSOCIAL LINKS\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Social Links</legend>\r\n		<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"headline\">Überschrift</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"headline\" type=\"text\" name=\"REX_INPUT_VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n		</div>\r\n	</div>\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"twitter\">Twitter-Link</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"twitter\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n		</div>\r\n	</div>\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"facebook\">Facebook-Link</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"facebook\" type=\"text\" name=\"REX_INPUT_VALUE[3]\" value=\"REX_VALUE[3]\" />\r\n		</div>\r\n	</div>\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"googleplus\">Google Plus-Link</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"googleplus\" type=\"text\" name=\"REX_INPUT_VALUE[4]\" value=\"REX_VALUE[4]\" />\r\n		</div>\r\n	</div>\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"dribble\">Dribble-Link</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"dribble\" type=\"text\" name=\"REX_INPUT_VALUE[5]\" value=\"REX_VALUE[5]\" />\r\n		</div>\r\n	</div>\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"instagram\">Instagram-Link</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"instagram\" type=\"text\" name=\"REX_INPUT_VALUE[6]\" value=\"REX_VALUE[6]\" />\r\n		</div>\r\n	</div>\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"youtube\">Youtube-Link</label>\r\n		<div class=\"col-sm-10\">\r\n			<input class=\"form-control\" id=\"youtube\" type=\"text\" name=\"REX_INPUT_VALUE[7]\" value=\"REX_VALUE[7]\" />\r\n		</div>\r\n	</div>\r\n</fieldset>\r\n','admin','admin','2016-05-25 12:15:32','2016-05-26 21:06:04','',0);
