  (4,'04 . Zweispalter Fullscreen (Registrieren)','<?php\r\ndefine(\'SITE_TYPE\', \'col2_full\');\r\n?>\r\nREX_TEMPLATE[5]\r\n',1,'admin','admin','2016-05-12 10:25:43','2016-05-12 10:25:43','{\"ctype\":{\"1\":\"Hauptspalte\",\"2\":\"Seitenspalte\"},\"modules\":{\"1\":{\"0\":\"1\",\"1\":\"2\",\"2\":\"3\",\"3\":\"5\",\"4\":\"8\",\"5\":\"9\",\"all\":0},\"2\":{\"0\":\"1\",\"1\":\"5\",\"2\":\"10\",\"all\":0},\"3\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (5,'05 . TEMPLATE','REX_TEMPLATE[6]\r\n\r\n<?php\r\n$content1 = $this->getArticle(\'1\');\r\n$content2 = $this->getArticle(\'2\');\r\n\r\n$headerpic = \'\';\r\nif (SITE_TYPE == \'col1_full\') {\r\n	$body_class = \' class=\"full-screen login\"\';\r\n	$navbar_id = \' id=\"register-navbar\"\';\r\n	$navbar_class = \' navbar-fixed-top\';\r\n	if ($this->getValue(\'art_file\') != \'\') {\r\n		$headerpic = \' style=\"background-image: url(\'.rex_url::base(\'index.php?rex_media_type=header&rex_media_file=\'.$this->getValue(\'art_file\')).\');\"\';\r\n	}\r\n} elseif (SITE_TYPE == \'col2_full\') {\r\n	$body_class = \' class=\"full-screen register\"\';\r\n	$navbar_id = \' id=\"register-navbar\"\';\r\n	$navbar_class = \' navbar-fixed-top\';\r\n	if ($this->getValue(\'art_file\') != \'\') {\r\n		$headerpic = \' style=\"background-image: url(\'.rex_url::base(\'index.php?rex_media_type=header&rex_media_file=\'.$this->getValue(\'art_file\')).\');\"\';\r\n	}\r\n} else {\r\n	$body_class = \' class=\"default\"\';\r\n	$navbar_id = \'\';\r\n	$navbar_class = \'\';\r\n}\r\n\r\necho \'\r\n<body\'.$body_class.$headerpic.\'>\';\r\n\r\nif (SITE_TYPE == \'col1\' || SITE_TYPE == \'col2\') {\r\n	 echo \'\r\n	 <div class=\"header-wrapper\">\';	\r\n}\r\n?>\r\n<!-- navbar -->\r\n	<nav class=\"navbar navbar-ct-transparent<?php echo $navbar_class; ?>\" role=\"navigation-demo\"<?php echo $navbar_id; ?>>\r\n	  <div class=\"container\">\r\n		<div class=\"navbar-header\">\r\n	        <button type=\"button\" class=\"navbar-toggle\" data-toggle=\"collapse\" data-target=\"#navigation\">\r\n			<span class=\"sr-only\">Toggle navigation</span>\r\n			<span class=\"icon-bar\"></span>\r\n			<span class=\"icon-bar\"></span>\r\n			<span class=\"icon-bar\"></span>\r\n		  </button>\r\n		  <a class=\"navbar-brand\" href=\"<?= rex_url::frontend() ?>\"><img src=\"<?= rex_url::base(\'resources/css/files/logo_white.svg\') ?>\" alt=\"REDAXO CMS\"></a>\r\n		</div>\r\n		<div class=\"navbar-collapse collapse\" id=\"navigation\">\r\n			<!-- Navigation -->\r\n			REX_TEMPLATE[8]\r\n			<!--/Navigation -->\r\n\r\n		</div><!--/.nav-collapse -->\r\n	  </div>\r\n	</nav>\r\n\r\n<?php\r\nif (SITE_TYPE == \'col1\' || SITE_TYPE == \'col2\') {\r\n	 echo \'\r\n	<!-- *******************************************************\r\n	Headerpic\r\n	******************************************************* -->\r\n	REX_TEMPLATE[7]\r\n</div>\';\r\n}\r\n?>\r\n\r\n<div class=\"wrapper\">\r\n\r\n	<?php\r\n	$loginstatus = \'\';\r\n	if ($ycom_user) {\r\n\r\n		$loginstatus = \'\r\n		<div id=\"notifications\" class=\"logged-in\">\r\n			<div class=\"alert alert-info\">\r\n				<div class=\"container\">\r\n					Hallo \'.rex_ycom_auth::getUser()->getValue(\'firstname\').\' \'.rex_ycom_auth::getUser()->getValue(\'name\').\', Du bist erfolgreich eingeloggt.\r\n				</div>\r\n			</div>\r\n			<div class=\"clearfix\"></div>\r\n		</div><!--  end notifications -->\';\r\n\r\n	} else {\r\n		// Benutzter besitzt die nötige Berechtigung nicht, ggf. Hinweis zeigen?\r\n	}\r\n\r\n	/*\r\n	Wenn das Template \"01 . Einspalter (Standard)\" gewählt ist, geht der Content-Container über die volle Breite.\r\n	*/\r\n	if (SITE_TYPE == \'col1\') {\r\n		echo $loginstatus.\'\r\n		<div class=\"main\">\r\n			<div class=\"section\">\r\n				<div class=\"container\">\r\n					\'.$content1.\'\r\n					REX_TEMPLATE[11]\r\n				</div>\r\n			</div>\r\n		</div>\';\r\n\r\n	/*\r\n	Wenn das Template \"02 . Zweispalter\" gewählt ist, gibt es eine Hauptspalte und eine Seitenspalte.\r\n	*/\r\n	} elseif (SITE_TYPE == \'col2\') {\r\n		echo $loginstatus.\'\r\n		<div class=\"section\">\r\n			<div class=\"container\">\r\n				<div class=\"row\">\r\n\r\n					<div class=\"col-md-8 bottom-border\">\r\n						\'.$content1.\'\r\n						REX_TEMPLATE[11]\r\n					</div>\r\n\r\n					<div class=\"col-md-3 col-md-offset-1\">\r\n						<!-- Subnavigation -->\';\r\n						$subnav_col = new rex_template(9);\r\n						include $subnav_col->getFile();\r\n						echo \'\r\n						<!-- /Subnavigation -->\r\n\r\n						<!-- ggf. weiterer Content -->\r\n						\'.$content2.\'\r\n					</div>\r\n\r\n				</div>\r\n			</div>\r\n		</div>\';\r\n\r\n		/*\r\n		Wenn das Template \"03 . Einspalter Fullscreen\" gewählt ist, gibt es mittig zentrierten Content auf einem Hintergrundbild\r\n		*/\r\n	} elseif (SITE_TYPE == \'col1_full\') {\r\n		echo \'\r\n			<div class=\"container\">\r\n				<div class=\"row\">\r\n					<div class=\"col-md-6 col-md-offset-3 col-sm-6 col-sm-offset-3 col-xs-10 col-xs-offset-1 \">\r\n						<div class=\"demo-card\">\r\n							\'.$content1.\'\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</div>\';\r\n\r\n	/*\r\n	Wenn das Template \"04 . Zweispalter Fullscreen\" gewählt ist, gibt es eine Textspalte und eine Formularspalte.\r\n	*/\r\n	} elseif (SITE_TYPE == \'col2_full\') {\r\n		echo \'\r\n			<div class=\"container\">\r\n				<div class=\"row\">\r\n					<div class=\"col-md-5 col-md-offset-1 col-sm-6 col-xs-12 info-horizontal\">\r\n						\'.$content1.\'\r\n					</div>\r\n					<div class=\"col-md-6 col-sm-6 col-xs-12\">\r\n						<div class=\"demo-card\">\r\n							\'.$content2.\'\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</div>\r\n			</div>\';\r\n\r\n	} ?>\r\n\r\n</div>\r\n	<!-- *******************************************************\r\n	 FOOTER\r\n	 ******************************************************* -->\r\n	REX_TEMPLATE[10]\r\n\r\n\r\n<!-- REDAXO 5 rocks! -->\r\n\r\n<!-- Javascripts -->\r\n<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/jquery-ui.min.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/bootstrap.min.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/checkbox.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/radio.js\') ?>\"></script>\r\n<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/select.js\') ?>\"></script>\r\n\r\n<script src=\"<?php echo rex_url::base(\'resources/js/photo_swipe/photoswipe.min.js\') ?>\"></script>\r\n<script src=\"<?php echo rex_url::base(\'resources/js/photo_swipe/photoswipe-ui-default.min.js\') ?>\"></script>\r\n<script src=\"<?php echo rex_url::base(\'resources/js/photo_swipe/init-gallery.js\') ?>\"></script>\r\n\r\n<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/rex-com-demo.js\') ?>\"></script>\r\n\r\n</body>\r\n</html>\r\n',0,'admin','admin','2016-06-03 14:54:21','2016-06-03 14:54:21','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (6,'00 . Header','<?php\r\n$ycom_user = rex_ycom_auth::getUser();\r\n\r\n// error_reporting(0);\r\nerror_reporting(E_ALL);\r\n// Das Error-Reporting sollte nur während der Entwicklung angeschaltet sein. Bei einer Live-Website sollte es auf 0 gestellt werden.\r\n\r\nheader(\'Content-Type: text/html; charset=utf-8\');\r\n?><!DOCTYPE html>\r\n<html lang=\"<?php echo rex_clang::getCurrent()->getCode(); ?>\">\r\n<head>\r\n	<meta charset=\"utf-8\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n	<?php\r\n	// Als Title-Tag den Artikelnamen, außer er wird manuell gesetzt\r\n	if ($this->getValue(\"art_title\") != \"\") {\r\n		$title = htmlspecialchars($this->getValue(\'art_title\'));\r\n	} else {\r\n		$title = htmlspecialchars($this->getValue(\'name\'));\r\n	}\r\n\r\n	echo \'\r\n	<title>\'.$title.\'</title>\';\r\n\r\n	// Keywords and description\r\n	// Wenn der aktuelle Artikel kein Keyword und Description besitzt, werden die aus dem Startartikel genommen\r\n	if ($this->getValue(\"art_keywords\") != \"\") {\r\n		$keywords = $this->getValue(\"art_keywords\");\r\n	} else {\r\n		$home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n		$keywords = $home->getValue(\'art_keywords\');\r\n	}\r\n\r\n	if ($this->getValue(\"art_description\") != \"\") {\r\n		$description = $this->getValue(\"art_description\");\r\n	} else {\r\n		$home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n		$description = $home->getValue(\'art_description\');\r\n	}\r\n\r\n	echo \'\r\n	<meta name=\"keywords\" content=\"\'.htmlspecialchars($keywords).\'\">\';\r\n\r\n	echo \'\r\n	<meta name=\"description\" content=\"\'.htmlspecialchars($description).\'\">\';\r\n	?>\r\n\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/bootstrap.min.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/base.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/rex-com-demo.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/fonts.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/font-awesome.min.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/themify-icons.css\') ?>\">\r\n\r\n	<!-- HTML5 shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n	<!--[if lt IE 9]>\r\n		<script src=\"http://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js\"></script>\r\n		<script src=\"http://oss.maxcdn.com/libs/respond.js/1.4.2/respond.min.js\"></script>\r\n	<![endif]-->\r\n	<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/jquery.js\') ?>\"></script>\r\n</head>\r\n',0,'admin','admin','2016-06-02 15:32:32','2016-06-02 15:32:32','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (7,'06 . Headerbild','<?php\r\n// Headerbild rekursiv einbinden.\r\n// Wenn der aktuelle Artikel kein Bild hat, wird das aus den Elternkategorien genommen.\r\n$header = \'\';\r\n$headerpic = $this->getValue(\'art_file\');\r\n\r\nif ($headerpic == \'\') {\r\n    $category = rex_article::getCurrent()->getCategory();\r\n    while ($header == \'\') {\r\n        if ($category == null) {\r\n            break;\r\n        }\r\n        $headerpic = $category->getValue(\'art_file\');\r\n        $category = $category->getParent();\r\n    } \r\n}\r\n\r\nif ($headerpic == \'\') {\r\n    $headerpic = rex_article::getSiteStartArticle()->getValue(\'art_file\');\r\n}\r\n\r\n\r\n$header = \'\r\n<div class=\"header\" style=\"background-image: url(\'.rex_url::base(\'index.php?rex_media_type=header&rex_media_file=\'.$headerpic).\');\r\n    \">\r\n	<div class=\"filter\"></div>\r\n	<div class=\"container text-center\">\r\n		<h1>\'.$this->getValue(\'name\').\'</h1>\';\r\n		if ($this->getValue(\'art_subtitle\') != \'\') {\r\n			$header .= \'\r\n			<h3>\'.$this->getValue(\'art_subtitle\').\'</h3>\';\r\n		}\r\n	$header .= \' \r\n	</div>\r\n</div>\';\r\n\r\nif (SITE_TYPE == \'col1\' || SITE_TYPE == \'col2\') {\r\necho $header;\r\n}\r\n?>\r\n',0,'admin','admin','2016-05-30 13:07:36','2016-05-30 13:07:36','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (8,'07 . Navigation','<?php\r\n$path = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = ((!empty($path[1])) ? $path[1] : \'\');\r\n$path2 = ((!empty($path[2])) ? $path[2] : \'\');\r\n\r\n$nav_main = \'\';\r\n\r\nforeach (rex_category::getRootCategories(true) as $lev1) { // wenn true, dann werden nur die Artikel mit Status online berücksichtigt\r\n\r\n	if (rex_ycom_auth::checkPerm($lev1) && $lev1->getId() != rex_article::getSiteStartArticleId()) { // Home soll nicht in der Navi auftauchen\r\n		\r\n		if ($lev1->getId() == 4) { // falls Profilkategorie, dann Profil-Foto\r\n			$profile_pic = \'profile_default.png\';\r\n			if ($ycom_user && rex_ycom_auth::getUser()->getValue(\'pic\') != \'\') $profile_pic = rex_ycom_auth::getUser()->getValue(\'pic\'); // prüfen, ob User eingeloggt ist und ob er ein Bild bhinterlegt hat\r\n			$profilelink = \'\r\n			<a href=\"#\" class=\"profile-photo dropdown-toggle\" data-toggle=\"dropdown\">\r\n                <div class=\"profile-photo-small\">\r\n                    <img src=\"index.php?rex_media_type=profile&rex_media_file=\'.$profile_pic.\'\" alt=\"Profilbild\" class=\"img-circle img-responsive img-no-padding\">\r\n                </div><span>Profil</span>\r\n            </a>\';\r\n		} else {\r\n			$profilelink = \'\r\n			<a class=\"dropdown-toggle\" href=\"\'.$lev1->getUrl().\'\">\'.htmlspecialchars($lev1->getValue(\'name\')).\'</a>\';\r\n		}\r\n\r\n		if ($lev1->getId() == $path1) {\r\n			$nav_main .= \'\r\n			<li class=\"dropdown active\">\'.$profilelink;\r\n		} else {\r\n   			$nav_main .= \'\r\n			<li class=\"dropdown\">\'.$profilelink;\r\n		}\r\n\r\n		// 1st level start\r\n		$lev1Size = sizeof($lev1->getChildren(true));\r\n\r\n			if ($lev1Size != \"0\") {\r\n\r\n				$nav_main .= \'\r\n				<ul class=\"dropdown-menu dropdown-menu-right\">\';\r\n\r\n					// START 2nd level categories\r\n					foreach ($lev1->getChildren() as $lev2):\r\n						if (rex_ycom_auth::checkPerm($lev2)) {\r\n\r\n							$nav_icon = ($lev2->getValue(\'art_nav_icon\') != \'\') ? \'<span class=\"icon-simple\"><i class=\"\'.$lev2->getValue(\'art_nav_icon\').\'\"></i></span> \' : \'\';\r\n\r\n							if ($lev2->getId() == $path2) {\r\n								$nav_main .= \'\r\n								<li class=\"dropdown-toggle active\"><a href=\"\'.$lev2->getUrl().\'\">\'.$nav_icon.htmlspecialchars($lev2->getValue(\'name\')).\'</a></li>\';\r\n							} else {\r\n								$nav_main .= \'\r\n								<li class=\"dropdown-toggle\"><a href=\"\'.$lev2->getUrl().\'\">\'.$nav_icon.htmlspecialchars($lev2->getValue(\'name\')).\'</a></li>\';\r\n							}\r\n						}\r\n					endforeach;\r\n\r\n				$nav_main .= \'\r\n				</ul>\';\r\n\r\n			}\r\n\r\n		$nav_main .= \'\r\n		</li>\';\r\n	}\r\n}\r\n\r\necho \'\r\n<ul class=\"nav navbar-nav navbar-right\">\r\n	\'.$nav_main.\'\r\n</ul>\';\r\n?>\r\n',0,'admin','admin','2016-06-04 00:04:48','2016-06-04 00:04:48','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (9,'08 . Subnavigation (Seitenspalte)','<?php\r\n$path = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = ((!empty($path[1])) ? $path[1] : \'\');\r\n$path2 = ((!empty($path[2])) ? $path[2] : \'\');\r\n\r\n$article = rex_article::get($path1);\r\n$articleUrl = $article->getUrl();\r\n$articleName = $article->getName();\r\n\r\n$nav = rex_ycom_navigation::factory();\r\n$subnav = $nav->get($path1,2,TRUE,TRUE);\r\n\r\nif ($subnav != \'\') {\r\n\r\n	echo \'\r\n	<h4 id=\"sidenav_title\"><a href=\"\'.$articleUrl.\'\">\'.$articleName.\'</a></h4>\';\r\n\r\n	echo \'\r\n	<div class=\"subnav\">\r\n	\'.$subnav.\'\r\n	</div\';\r\n\r\n}\r\n?>\r\n',0,'admin','admin','2016-05-25 13:30:54','2016-05-25 13:30:54','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (10,'09 . Footer Hilfstemplate','<footer class=\"section-dark\">\r\n	<div class=\"container\">\r\n		<nav class=\"pull-left\">\r\n			REX_ARTICLE[id=6]\r\n		</nav>\r\n		<div class=\"copyright pull-right\">\r\n			&copy; 2016 – REDAXO CMS <i class=\"fa fa-heart heart\"></i> ya all!\r\n		</div>\r\n	</div>\r\n</footer>',0,'admin','admin','2016-05-29 21:16:44','2016-05-29 21:16:44','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0),
  (11,'10 . Kommentare','<?php\r\n// Kommentarfunktion für diesen Artikel freigeschaltet?\r\nif ($this->getValue(\'art_comment\') == \'|true|\') {\r\n	// Ist der User eingeloggt?\r\n	if ($ycom_user) {\r\n\r\n		$sql = rex_sql::factory();\r\n		$sql_query = \'\r\n			SELECT\r\n				firstname, name, rex_ycom_user.status, pic, comment, rex_ycom_user.status, stamp,\r\n				DATE_FORMAT(stamp, \"%e. %m. %Y, %H:%i\") as comment_date\r\n			FROM\r\n				rex_ycom_user, rex_ycom_comment\r\n			WHERE\r\n				rex_ycom_comment.article = \"\'.$this->article_id.\'\"\r\n			AND\r\n				rex_ycom_comment.user_id = rex_ycom_user.id\r\n			AND\r\n				rex_ycom_comment.status = 1\r\n			ORDER BY\r\n				stamp\';\r\n\r\n		$sql->setQuery($sql_query);\r\n\r\n		echo \'\r\n		<div class=\"section section-nude-gray\" id=\"comments\">\r\n			<div class=\"media-area\">\r\n				<h3 class=\"text-center\">Kommentare (\'.$sql->getRows().\')</h3>\r\n				<hr>\';\r\n\r\n				for ($i=0; $i<$sql->getRows(); $i++) {\r\n\r\n					$profile_pic = \'profile_default.png\';\r\n					if ($sql->getValue(\'pic\') != \'\') $profile_pic = $sql->getValue(\'pic\');\r\n\r\n\r\n		                    echo \'\r\n		                    <div class=\"media\">\r\n		                          <div class=\"avatar\">\r\n		                                <img class=\"media-object\" alt=\"\'.$sql->getValue(\'firstname\').\' \'.$sql->getValue(\'name\').\'\" src=\"\'.rex_url::base(\'index.php?rex_media_type=profile&rex_media_file=\'.$profile_pic.\'\').\'\">\r\n		                          </div>\r\n		                          <div class=\"media-body\">\r\n		                                <h5 class=\"media-heading\">\'.$sql->getValue(\'firstname\').\' \'.$sql->getValue(\'name\').\'</h5>\r\n		                                <div class=\"pull-right\">\r\n		                                    <h6 class=\"text-muted\">\'.$sql->getValue(\'comment_date\').\'</h6>\r\n		                                </div>\r\n\r\n		                                <p>\'.nl2br($sql->getValue(\'comment\')).\'</p>\r\n\r\n		                          </div>\r\n		                    </div> <!-- end media -->\';\r\n					\r\n					$sql->next();\r\n				}\r\n\r\n			echo \'\r\n			</div> <!-- end media-area -->\r\n		</div>\r\n		<div id=\"comments-end\"></div>\';\r\n\r\n		// Formulare zeigen\r\n		$yform = new rex_yform();\r\n		$yform->setObjectparams(\'form_skin\', \'bootstrap\');\r\n		$yform->setObjectparams(\'form_anchor\', \'comments-end\');\r\n		$yform->setObjectparams(\'form_showformafterupdate\', 0);\r\n		$yform->setObjectparams(\'real_field_names\', true);\r\n		$yform->setObjectparams(\'form_action\', rex_getUrl($this->article_id));\r\n\r\n		$yform->setValueField(\'hidden\', array(\"article\", $this->article_id));\r\n		$yform->setValueField(\'hidden\', array(\"user_id\", rex_ycom_auth::getUser()->getValue(\'id\')));\r\n		$yform->setValueField(\'hidden\', array(\"status\", \"1\"));\r\n		$yform->setValueField(\'hidden\', array(\"stamp\", date(\"YmdHis\")));\r\n\r\n		$yform->setValueField(\'textarea\', array(\"comment\",\"Kommentar schreiben\"));\r\n		$yform->setValidateField(\'empty\', array(\"comment\",\"Bitte Kommentar eingeben\"));\r\n\r\n		$yform->setActionField(\'db\', array(\'rex_ycom_comment\'));\r\n		// ggf. Kommentar auch an Admin setzen:\r\n		// $yform->setActionField(\'email\', array(\'commrent_admin\', \'emaillabel\', \'your_email.de\'));\r\n		$yform->setActionField(\'showtext\', array(\"Kommentar wurde hinzugefügt.\",\'<div class=\"alert alert-success\">\',\"</div>\",\"1\"));\r\n		echo $yform->getForm();\r\n\r\n	}\r\n}\r\n?>\r\n',0,'admin','admin','2016-06-04 17:53:20','2016-06-04 17:53:20','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0);
