INSERT INTO `rex_yform_email_template` VALUES 
  (1,'access_request_de','noreply@redaxo.org','REDAXO Community Demo','REDAXO Community Demo: Bitte bestätige Deine Anmeldung','Du hast Dich mit folgendem Zugang angemeldet:\r\n\r\nLogin: REX_YFORM_DATA[field=\"email\"]\r\nName: REX_YFORM_DATA[field=\"firstname\"]\r\nNachname: REX_YFORM_DATA[field=\"name\"]\r\nE-Mail: REX_YFORM_DATA[field=\"email\"]\r\n\r\nBitte bestätige Deine Anmeldung mit einem Klick auf folgenden Link:\r\n\r\n<?php \r\n\r\n$url = rex::getServer().rex_getUrl(16,\'\',[ \'activation_key\' => REX_YFORM_DATA[field=\"activation_key\"], \'email\' => REX_YFORM_DATA[field=\"email\"] ], \'&\'); \r\n\r\n$url = str_replace([\'./\',\'//\',\'http:/\'],[\'\',\'/\',\'http://\'],$url);\r\necho $url;\r\n\r\n?>','',''),
  (2,'access_confirmation_de','noreply@redaxo.org','REDAXO Community Demo','REDAXO Community Demo: Dein Zugang wurde freigeschaltet','Dein Zugang wurde freigeschaltet.\r\nDu kannst Dich nun einloggen:\r\n<?php\r\n$url = rex::getServer().rex_getUrl(5);\r\n\r\n$url = str_replace([\'./\',\'//\',\'http:/\'],[\'\',\'/\',\'http://\'],$url);\r\necho $url;\r\n?>\r\n\r\nDu könntest anschließend auf Deiner Profilseite ein Foto von Dir hochladen ... ?','',''),
  (3,'request_password_de','noreply@redaxo.org','REDAXO Community Demo','REDAXO Community Demo: Du willst Dein Passwort zurücksetzen','Guten Tag REX_YFORM_DATA[field=\"firstname\"] REX_YFORM_DATA[field=\"name\"], \r\n\r\nDu hast ein neues Passwort angefordert.\r\n\r\nBitte klicke auf folgenden Link, um Dein Passwort zurückzusetzen:\r\n<?php \r\n$url = rex::getServer().rex_getUrl(19,\'\',[ \'activation_key\' => REX_YFORM_DATA[field=\"activation_key\"], \'email\' => REX_YFORM_DATA[field=\"email\"] ], \'&\'); \r\n\r\n// ugly hack to strip \"./\"\r\n$url = str_replace([\'./\',\'//\',\'http:/\'],[\'\',\'/\',\'http://\'],$url);\r\necho $url;\r\n?>\r\n\r\nSolltest Du diese E-Mail nicht angefordert haben, bzw. Dein Passwort nicht vergessen haben, kannst Du diese E-Mail ignorieren.','',''),
  (4,'access_admin_confirm_de','noreply@redaxo.org','REDAXO Community Demo','REDAXO Community Demo: Anmeldung bestätigt','Der User mit der Email REX_YFORM_DATA[field=\"email\"] hat seine Anmeldung bestätigt. Bitte die entsprechenden Gruppenrechte einstellen und freischalten:\r\n<?php echo rex::getServer(); ?>redaxo/index.php?page=yform/manager/data_edit&table_name=rex_ycom_user&data_id=REX_YFORM_DATA[field=\"id\"]&func=edit\r\n\r\nAnschließend dem User durch diesen Link eine Nachricht senden, dass sein Account freigeschaltet ist:\r\n<?php \r\n$url = rex::getServer().rex_getUrl(17,\'\',[ \'activation_key\' => REX_YFORM_DATA[field=\"activation_key\"], \'email\' => REX_YFORM_DATA[field=\"email\"] ], \'&\'); \r\n\r\n// ugly hack to strip \"./\"\r\n$url = str_replace([\'./\',\'//\',\'http:/\'],[\'\',\'/\',\'http://\'],$url);\r\necho $url;\r\n?>\r\n\r\n','','');
